const HEADER_SIZE = 18;
const ID = "Art-Net\0";
const ArtDmxOpCode = 0x5000;
const ProtVer = 14;
context.sequence = context.sequence || 0;
const Physical = 0;

var header = new Buffer(HEADER_SIZE);
header.fill(0);

header.write(ID);
header.writeUInt16LE(ArtDmxOpCode, 8);
header.writeUInt16BE(ProtVer, 10);
header.writeUInt8(Physical, 13);

// Parse incoming Art-Net packets
if (msg.payload.slice(0, 8).toString() == ID) {
    var Opcode = msg.payload.readUInt16LE(8);
    if (Opcode == ArtDmxOpCode) {
        var Universe = msg.payload.readUInt16LE(14);
        var Length = msg.payload.readUInt16BE(16);
        msg.topic = Universe;
        msg.payload = msg.payload.slice(HEADER_SIZE, Length + HEADER_SIZE);
        node.send(msg);
    }
// Format incoming buffer or array into Art-Net packet
} else {
    context.sequence++;
    header.writeUInt8(context.sequence, 12);
    if (context.sequence >= 255)
        context.sequence = 0;

    var Universe = msg.topic;
    var Length = msg.payload.length;
    if (Array.isArray(msg.payload)) {
        msg.payload = new Buffer(msg.payload);
    }
    header.writeUInt16LE(Universe, 14);
    header.writeUInt16BE(Length, 16);

    msg.payload = Buffer.concat([header, msg.payload]);
    node.send(msg);
}

